import {React} from "react";
import {Link} from "react-router-dom";
import styled from "styled-components";
import Header from "./Header";
import MASHA from "../public/MASHA.png";




const Home = () => {
    return (
        <PageContainer>
            <Header/>
            <img src={MASHA} height='400px' alt={'pic'} style={{borderRadius: "20px", zIndex: "1"}}></img>
        </PageContainer>
    )
}


const PageContainer = styled.div`
    display: flex;
    overflow: hidden;
    width: 100%;
    flex-direction: column;
    justify-content: start;
    align-items: stretch;
    color: #fff;
    background-color: #0A0A0A;
    gap: 0;
`;

const LandingContainer = styled.div`
    height: 100vh;
    text-align: start;
    display: flex;
    flex-direction: column;
    overflow: hidden;
    justify-content: center;
    align-items: end;
    padding: 0 50px;
    color: #fff
`;

const HomeContainer = styled.div`
    height: 100vh;
    text-align: start;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    padding-left: 120px;
    padding-right: 120px;
    background-color: #0A0A0A;
    color: #fff;
    z-index: 0;
`;

const DescriptionContainer = styled.div`
    max-height: 100vh;
    min-height: 75vh;
    text-align: start;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    padding-left: 120px;
    padding-right: 120px;
    background-color: #0A0A0A;
    color: #fff;
    z-index: 0;
`;

const SeparatorContainer = styled.div`
    height: 20vh;
    overflow: hidden;
    text-align: start;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    margin: 50px 0;
    background-color: #0A0A0A;
    color: #fff;
    z-index: 0;
`;

const Title = styled.div`
    font-size: 96px;
    font-weight: 500;
    color: #fff;
    letter-spacing: 15px;
    text-shadow: 1px 1px 5px rgba(0,0,0,1);
    z-index: 1;
`;

const TitleGreen = styled.div`
    font-size: 96px;
    font-weight: 500;
    color: #0AD70A;
    letter-spacing: 15px;
    text-shadow: 1px 1px 5px rgba(0,0,0,1);
    z-index: 1;
`;

const SubTitle = styled.div`
    font-size: 32px;
    font-weight: 200;
    padding: 0px 10px;
    color: #fff;
    text-shadow: 1px 1px 5px rgba(0,0,0,0.5);
    z-index: 1;
`;


const StyledButton = styled.button`
    background-color: #0AD70A;
    color: #0a0a0a;
    //background-color: #fff;
    margin-top: 30px;
    margin-right: 10px;
    font-size: 18px;
    font-weight: 500;
    padding: 20px 40px;
    border: 2px solid #0AD70A;
    //border-radius: 30px;
    //text-shadow: 2px 1px 2px rgba(0,0,0,0.3);
    //box-shadow: 2px 4px 5px rgba(0,0,0,0.7);
    z-index: 1;
    transition: all 0.2s ease-in-out;
    &:hover {
        background-color: #0a0a0a;
        color: #0AD70A;
        border: 2px solid #0AD70A;
        transition: all 0.2s ease-in-out;
    }
`;


const Styledvideo = styled.video`
    position: absolute;
    top: -75px;
    left: -0px;
`;



export default Home;
