import {React} from "react";
import {Link} from "react-router-dom";
import styled from "styled-components";
import Header from "./Header";


import Project_1 from "./Project_1.jpg";
import MASHADAMID from "./MASHADAMID.jpg";


const Project1 = () => {
    return (
        <PageContainer>
            <Header/>
            <img src={Project_1} style={{height: "100vh"}} alt="Project_1"/>
            <Exp3Container>
                <h1>"MADE"</h1>
                <div>
                    <h2>INTERACTIVE INSTALLATION</h2>
                    <h3>“MADE” project: Mixing Physical Textures with Digital Art.</h3>
                </div>
                <p>Interaction Design, <br/>
                    Interactive Installations,
                    Projection Mapping.
                    <br/>
                    <br/>
                    August 2021</p>
            </Exp3Container>
            <Exp4Container>
                <div style={{width: "3fr"}}>
                    <h3 style={{color: "#FF5858"}}>Team</h3>
                    <h3 style={{textDecoration: "underline"}}>EGOR NELYUBIN</h3>
                    <h3 style={{textDecoration: "underline"}}>AMIR KARIM</h3>
                    <h3 style={{textDecoration: "underline"}}>DAVID PETRO</h3>
                </div>
                <div>
                    <h3 style={{color: "#FF5858"}}>Mentor</h3>
                    <h3><MarkNone>DANIEL SCHIEBEL</MarkNone> FROM <MarkNone>RED PAPER HEART</MarkNone></h3>
                </div>
                <div>
                    <h3 style={{color: "#FF5858"}}>My Role</h3>
                    <h3>INTERACTION DESIGNER</h3>
                </div>
                <div>
                    <h3 style={{color: "#FF5858"}}>Time</h3>
                    <h3>3 WEEKS</h3>
                </div>
            </Exp4Container>
            <OrangeContainer>
                <h3 style={{textTransform: "uppercase"}}>"Digital Surfaces" is a project that<br/>translates the diverse textures of <br/> Barcelona into a digital experience.
                    <br/>
                    <br/>
                    We brainstormed the concept, built <br/>prototypes, and pushed the <br/>boundaries to make it happen. </h3>
            </OrangeContainer>
            <GalleryContainer>
                <img src={MASHADAMID} alt="MASHADAMID" style={{width: "100%"}}/>
            </GalleryContainer>
            <DescriptionContainer>
                <h4></h4>
            </DescriptionContainer>
        </PageContainer>
    );

}

const PageContainer = styled.div`
    display: flex;
    //overflow: hidden;
    width: 100vw;
    flex-direction: column;
    justify-content: start;
    align-items: stretch;
    color: #fff;
    background-color: #171819;
`;

const LandingContainer = styled.div`
    scale: 1;
    background: linear-gradient(to right, #F857A6, #FF5858);
    height: 100%;
    text-align: start;
    display: flex;
    flex-direction: row;
    overflow: hidden;
    justify-content: start;
    align-items: start;
    color: #fff
`;

const Exp3Container = styled.div`
    overflow: hidden;
    text-align: start;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    grid-gap: 100px;
    justify-content: start;
    align-items: start;
    padding: 90px 90px 0 90px;
`;

const Exp4Container = styled.div`
    overflow: hidden;
    text-align: start;
    display: grid;
    grid-template-columns: 1fr 1.2fr 1fr 0.8fr;
    grid-gap: 75px;
    justify-content: start;
    align-items: start;
    padding: 90px;

`;

const GalleryContainer= styled.div`
    max-height: 100vh;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    padding-left: 90px;
    padding-right: 90px;
    padding-top: 90px;
    padding-bottom: 90px;
    color: #fff;
    z-index: 0;
`;


const HomeContainer = styled.div`
    height: 100%;
    text-align: start;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    grid-gap: 10px;
    gap: 20px;
    justify-content: center;
    align-items: center;
    padding-inline: 95px;
    background-color: #171819;
    color: #fff;
    z-index: 0;
`;

const OrangeContainer = styled.div`
    max-height: 100vh;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    padding-left: 90px;
    padding-right: 90px;
    padding-top: 90px;
    padding-bottom: 90px;
    gap: 90px;
    background-color: #FF5858;
    color: #171819;
    z-index: 0;
`;

const DescriptionContainer = styled.div`
    max-height: 100vh;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    padding-left: 90px;
    padding-right: 90px;
    padding-top: 90px;
    padding-bottom: 90px;
    gap: 90px;
    color: #fff;
    z-index: 0;
`;

const MarkBrightPink = styled.mark`
    background-color: #FFC8E3;
    color: #171819;
    font-weight: 400;
`;

const MarkPink = styled.mark`
    background-color: #F857A6;
    color: #171819;
    font-weight: 400;
`;

const MarkOrange = styled.mark`
    background-color: #FF5858;
    color: #171819;
    font-weight: 400;
`;

const MarkNone = styled.mark`
    text-decoration: underline;
    color: white;
    background-color: rgba(0, 0, 0, 0);
`;

const SeparatorContainer = styled.div`
    overflow: hidden;
    height: 300px;
    text-align: start;
    display: flex;
    flex-direction: row;
    justify-content: start;
    align-items: center;
    padding-left: 90px;
    background-color: #171819;
    color: #fff;
    z-index: 0;
`;

const Footer = styled.div`
    overflow: hidden;
    text-align: start;
    display: flex;
    flex-direction: column;
    justify-content: stretch;
    align-items: center;
    padding-top: 90px;
    padding-inline: 90px;
    padding-bottom: 50px;
    gap: 90px;
    background-color: #fff;
    color: #171819;
    z-index: 0;
`;

const Title = styled.div`
    font-size: 100px;
    font-weight: 400;
    color: #fff;
    //text-shadow: 1px 1px 5px rgba(0,0,0,1);
    z-index: 1;
`;


const SubTitle = styled.div`
    font-size: 32px;
    font-weight: 200;
    padding: 0px 10px;
    color: #fff;
    text-shadow: 1px 1px 5px rgba(0,0,0,0.5);
    z-index: 1;
`;


const StyledButton = styled.button`
    background-color: #0AD70A;
    color: #0a0a0a;
    //background-color: #fff;
    margin-top: 30px;
    margin-right: 10px;
    font-size: 18px;
    font-weight: 500;
    padding: 20px 40px;
    border: 2px solid #0AD70A;
    //border-radius: 30px;
    //text-shadow: 2px 1px 2px rgba(0,0,0,0.3);
    //box-shadow: 2px 4px 5px rgba(0,0,0,0.7);
    z-index: 1;
    transition: all 0.2s ease-in-out;
    &:hover {
        background-color: #0a0a0a;
        color: #0AD70A;
        border: 2px solid #0AD70A;
        transition: all 0.2s ease-in-out;
    }
`;


const Styledvideo = styled.video`
    position: absolute;
    top: -75px;
    left: -0px;
`;

const StyledLink = styled(Link)`
    color: #171819;
    text-decoration: none;
    position: relative;
    margin-top: 10px;
    padding-bottom: 10px;
`;

export default Project1;
