import {React} from "react";
import {Link} from "react-router-dom";
import styled from "styled-components";
import Header from "./Header";
import Footer from "./Footer";
import YourImage from './Screenshot.jpg';
import MyVideo from './Sequence.mp4';
import Prototype from './Pasted Graphic.png';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faArrowDown} from "@fortawesome/free-solid-svg-icons";
import Animation from "./Animation";
import TigerIcon from "./tiger icon.png";
import RoryIcon from "./Rory icon.png";
import CollinIcon from "./Collin icon.png";
import MentorIcon from "./Mentor icon.png";
import PrototypeFront from './front mockup.png';



const Home = () => {
    return (
        <PageContainer>
            <Header />

            <LandingContainer>
                {/*<Styledvideo width="100%" autoPlay muted loop>*/}
                {/*    <source src={MyVideo} type="video/mp4"/>*/}
                {/*    Your browser does not support the video tag.*/}
                {/*</Styledvideo>*/}
                <canvas id="canvas" style={{position: "absolute", left:750}}>

                </canvas>
                <div style={{display: "flex", flexDirection: "row", marginTop: "100px"}}>
                    <Title>SWING</Title>
                    <TitleGreen>MENTOR</TitleGreen>
                </div>
                <SubTitle>Your personal AI golf coach</SubTitle>
                <StyledButton>Start Now</StyledButton>
            </LandingContainer>
            <FontAwesomeIcon icon={faArrowDown} style={{position: 'relative', top: -30, scale: "150%", color: '#fff'}}/>
            <HomeContainer>
                <div>
                    <div style={{fontSize: 72, fontWeight: 500}}>Train.</div>
                    <div style={{fontSize: 72, fontWeight: 500}}>Anytime.</div>
                    <div style={{fontSize: 72, fontWeight: 500}}>Anywhere.</div>
                    <p style={{fontSize: '20px'}}>With your personal AI-mentor in your pocket</p>
                    <StyledButton>Download <FontAwesomeIcon icon={faArrowDown}/></StyledButton>
                </div>
                <img src={Prototype} height='600px' alt={'pic'}></img>
            </HomeContainer>
            <SeparatorContainer>
                <Animation />
                <div style={{position: "absolute", display: "flex", flexDirection: "row"}}>
                    <Title style={{fontSize: "80px"}}>OUR AI-</Title>
                    <TitleGreen style={{fontSize: "80px"}}>MENTORS</TitleGreen>
                </div>
            </SeparatorContainer>
            <DescriptionContainer>
                <div style={{maxWidth: "630px", gap: '33px', display: 'flex', flexDirection: 'column'}}>
                    <div style={{fontSize: 72, fontWeight: 500}}>Tiger Woods</div>
