import {React} from'react';
import styled from "styled-components";
import {Link} from'react-router-dom';
import { useNavigate } from'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faInstagram} from "@fortawesome/free-brands-svg-icons";
import { useEffect, useState } from'react';

const Header = () => {
    const [isScrolled, setIsScrolled] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            if (window.scrollY > 0) {
                setIsScrolled(true);
            } else {
                setIsScrolled(false);
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return(
        <HeaderContainer  isScrolled={isScrolled}>
            <NavBar>
                <StyledLink to={'/'}>PROJECTS</StyledLink>
                <StyledLink to={'/mentors'}>ABOUT</StyledLink>
                <StyledLink to={'/about'}>SKETCHBOOK</StyledLink>
            </NavBar>
            <Logo>
                MASHA SREDINA
            </Logo>
            <UserContainer>
                <FontAwesomeIcon icon={faInstagram} />
                <p>linkedin</p>
            </UserContainer>
        </HeaderContainer>
    )
};

const HeaderContainer = styled.div`
    position: fixed;
    width: 100%;
    height: 59px;
    color: #000;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    padding: 10px 50px;
    font-weight: 400;
    z-index: 1000;
    transition: background-color 0.3s;
    background: linear-gradient(to right, #F857A6, #FF5858);
`;

const StyledLink = styled(Link)`
    color: #000;
    text-decoration: none;
    position: relative;
    margin-top: 10px;
    padding-bottom: 10px;

    &:hover::before {
        width: 100%;
    }

    &::before {
        content: '';
        position: absolute;
        bottom: 0;
        left: 0;
        width: 0;
        height: 2px;
        background-color: #0AD70A;
        transition: width 0.3s ease;
    }
`;

const Logo = styled.div`
    display: flex;
    flex-direction: row;
    font-size: 40px;
    font-weight: 400;
    align-items: center;
    justify-content: center;
    margin: 0 auto;

`;


const NavBar = styled.nav`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    font-size: 20px;
    gap: 30px;
    margin: 0 20px;
    align-items: stretch;

`;

const UserContainer = styled.div`
    display: flex;
    color: #000;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    font-size: 20px;
    //margin-left: 120px;
    //padding: 0 15px;
    gap: 15px;
    margin: 0 auto;
`;
export default Header;
