import {React, useEffect} from "react";
import {Link, useLocation} from "react-router-dom";
import styled from "styled-components";
import Header from "./Header";
import Footer from "./Footer";


import Sloth_landing_page from "./Sloth_landing_page.jpg";
import sloth_gallery_1 from "./sloth_gallery_1.png";




const Sloth = () => {
    return(
        <PageContainer>
           <Header/>
           <img src={Sloth_landing_page} alt="Sloth" style={{width: "100%"}}/>
            <Exp3Container>
                <h1>"SLOTH COFFEE"</h1>
                <div>
                    <h2>REBRANDING</h2>
                    <h3>“SLOTH COFFEE” IMAGINE HAVING YOUR SECRET JUNGLE IN THE MIDDLE OF THE CITY!</h3>
                </div>
                <p>Re/Branding,<br/>
                    Brand strategy, <br/>
                    Concepting, <br/>
                    Visual design.
                    <br/>
                    <br/>
                    April 2022</p>
            </Exp3Container>
            <Exp4Container>
                <div style={{width: "3fr"}}>

                </div>
                <div>
                    <h3 style={{color: "#F857A6"}}>Mentor</h3>
                    <h3><MarkNone>DIEGO MARINI</MarkNone> FROM <MarkNone>YUMMY COLORS</MarkNone></h3>
                </div>
                <div>
                    <h3 style={{color: "#F857A6"}}>My Role</h3>
                    <h3>BRAND IDENTITY DESIGNER</h3>
                </div>
                <div>
                    <h3 style={{color: "#F857A6"}}>Time</h3>
                    <h3>3 WEEKS</h3>
                </div>
            </Exp4Container>
            <PinkContainer style={{paddingInline: "272px"}}>
                <h3 style={{textTransform: "uppercase"}}>From a failed gift shop collab rose the world's 1st cafe to blend animation, fashion, and games. And I didn't just stop at a rebrandingI I transformed it into a secret jungle in the heart of the city where you can enjoy
                    your coffee & leave all your worries behind.</h3>
            </PinkContainer>
            <DescriptionContainer>
                <h4>OVERVIEW</h4>
                <p1>
                    Gift Coffee, named after its initial collaboration with a gift shop that eventually went out of business, used to be one of the trendiest cafes in Moscow before it faced bankruptcy. It was a jungle in the middle of a megalopolis - a cozy coffee shop with a friendly atmosphere, fluffy sloths, and lots of greenery.
                </p1>
            </DescriptionContainer>
            <GalleryContainer>
                <img src={sloth_gallery_1} alt="Sloth" style={{width: "100%"}}/>
            </GalleryContainer>
            <DescriptionContainer style={{display: "grid", gridTemplateColumns: "1.2fr 1fr", gridGap: "30px" }}>
                <h4>SECRETS OF OUR
                    COMPETITORS:</h4>
                <p1>
                    Our jungle cafe aims to differentiate from direct and indirect competitors in the coffee industry by prioritising consistent and immersive customer experiences, building a strong brand community, and leveraging unique selling points like our jungle-themed visuals and colourful drinks. Direct competitors have issues with opening hours and social media despite offering beautiful visuals and community building. Indirect competitors offer a different experience for customers.
                </p1>
            </DescriptionContainer>
        </PageContainer>
    )
};

const PageContainer = styled.div`
    display: flex;
    //overflow: hidden;
    width: 100vw;
    flex-direction: column;
    justify-content: start;
    align-items: stretch;
    color: #171819;
    background-color: #fff;
`;

const LandingContainer = styled.div`
    scale: 1;
    background: linear-gradient(to right, #F857A6, #FF5858);
    height: 100%;
    text-align: start;
    display: flex;
    flex-direction: row;
    overflow: hidden;
    justify-content: start;
    align-items: start;
    color: #fff
`;

const Exp3Container = styled.div`
    overflow: hidden;
    text-align: start;
    display: grid;
    grid-template-columns: 1fr 1.4fr 0.6fr;
    grid-gap: 100px;
    justify-content: start;
    align-items: start;
    padding: 90px 90px 0 90px;
`;

const Exp4Container = styled.div`
    overflow: hidden;
    text-align: start;
    display: grid;
    grid-template-columns: 1fr 1.2fr 1fr 0.8fr;
    grid-gap: 75px;
    justify-content: start;
    align-items: start;
    padding: 90px;

`;

const GalleryContainer= styled.div`
    max-height: 100vh;
    min-height: 10vh;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    padding-left: 90px;
    padding-right: 90px;
    padding-top: 90px;
    padding-bottom: 90px;
    color: #fff;
    z-index: 0;
`;

const GalleryContainerWhite= styled.div`
    max-height: 100vh;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    padding-left: 90px;
    padding-right: 90px;
    padding-top: 90px;
    //padding-bottom: 90px;
    background-color: #fff;
    z-index: 0;
`;


const HomeContainer = styled.div`
    height: 100%;
    text-align: start;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    grid-gap: 10px;
    gap: 20px;
    justify-content: center;
    align-items: center;
    padding-inline: 95px;
    background-color: #171819;
    color: #fff;
    z-index: 0;
`;

const PinkContainer = styled.div`
    max-height: 100vh;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    padding-left: 90px;
    padding-right: 90px;
    padding-top: 90px;
    padding-bottom: 90px;
    gap: 90px;
    background-color: #F857A6;
    color: #171819;
    z-index: 0;
`;


const DescriptionContainer = styled.div`
    //max-height: 100vh;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: start;
    padding-left: 90px;
    padding-right: 90px;
    padding-top: 90px;
    padding-bottom: 90px;
    gap: 120px;
    color: #171819;
    z-index: 0;
`;

const DescriptionContainerWhite = styled.div`
    max-height: 100vh;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: start;
    padding-left: 90px;
    padding-right: 90px;
    padding-top: 90px;
    padding-bottom: 90px;
    gap: 90px;
    background-color: #fff;
    color: #171819;
    z-index: 0;
`;

const MarkBrightPink = styled.mark`
    background-color: #FFC8E3;
    color: #171819;
    font-weight: 400;
`;

const MarkPink = styled.mark`
    background-color: #F857A6;
    color: #171819;
    font-weight: 400;
`;

const MarkOrange = styled.mark`
    background-color: #FF5858;
    color: #171819;
    font-weight: 400;
`;

const MarkNone = styled.mark`
    text-decoration: underline;
    color: #171819;
    background-color: rgba(0, 0, 0, 0);
`;

const SeparatorContainer = styled.div`
    overflow: hidden;
    height: 300px;
    text-align: start;
    display: flex;
    flex-direction: row;
    justify-content: start;
    align-items: center;
    padding-left: 90px;
    background-color: #171819;
    color: #fff;
    z-index: 0;
`;


const Title = styled.div`
    font-size: 100px;
    font-weight: 400;
    color: #fff;
    //text-shadow: 1px 1px 5px rgba(0,0,0,1);
    z-index: 1;
`;


const SubTitle = styled.div`
    font-size: 32px;
    font-weight: 200;
    padding: 0px 10px;
    color: #fff;
    text-shadow: 1px 1px 5px rgba(0,0,0,0.5);
    z-index: 1;
`;


const Styledvideo = styled.video`
    position: absolute;
    top: -75px;
    left: -0px;
`;

const StyledLink = styled(Link)`
    color: #171819;
    text-decoration: none;
    position: relative;
    margin-top: 10px;
    padding-bottom: 10px;
`;


export default Sloth;
