    align-items: start;
    padding: 90px;
`;

const GalExp4Container = styled.div`
    overflow: hidden;
    text-align: start;
    display: grid;
    grid-template-columns: 0.7fr 0.75fr 1fr 0.8fr;
    grid-column-gap: 90px;
    grid-row-gap: 30px;
    justify-content: center;
    align-items: start;
    padding: 90px;

`;

const NiceList = styled.ul`
    list-style-type: none;
    padding: 0;
    margin: 0;
    font-size: 25px;
    gap: 50px;
`;

