    margin-bottom: 30px;
    &::before {
        content: '•';
        position: absolute;
        font-size: 50px;
        left: -20px;
        top: -18px;
        color: #F857A6;
    }
`;

const BrightPinkItem = styled.li`
    position: relative;
    padding-left: 20px;
    margin-bottom: 30px;
    &::before {
        content: '•';
        position: absolute;
        font-size: 50px;
        left: -20px;
