        content: '•';
        position: absolute;
        font-size: 50px;
        left: -20px;
        top: -18px;
        color: #171819;
    }
`;

const GalleryContainer= styled.div`
    max-height: 100vh;
    min-height: 10vh;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    padding-left: 90px;
    padding-right: 90px;
    padding-top: 90px;
    padding-bottom: 90px;
    color: #fff;
    z-index: 0;
`;

const GalleryContainerWhite= styled.div`
    max-height: 100vh;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    padding-left: 90px;
    padding-right: 90px;
    padding-top: 90px;
    //padding-bottom: 90px;
    background-color: #fff;
    z-index: 0;
`;

const MarkBrightPink = styled.mark`
background-color: #FFC8E3;
color: #171819;
font-weight: 400;
line-height: 1.5;
`;

const MarkPink = styled.mark`
background-color: #F857A6;
color: #171819;
font-weight: 400;
line-height: 1.5;
`;

const MarkOrange = styled.mark`
background-color: #FF5858;
color: #171819;
font-weight: 400;
line-height: 1.5;    
`;


const HomeContainer = styled.div`
    height: 100%;
    text-align: start;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    grid-gap: 10px;
    gap: 20px;
    justify-content: center;
    align-items: center;
    padding-inline: 95px;
    background-color: #171819;
    color: #fff;
    z-index: 0;
`;

const PinkContainer = styled.div`
    max-height: 100vh;
    display: flex;
