    display: flex;
    flex-direction: row;
    justify-content: start;
    align-items: end;
    padding: 90px;
    background-color: #57B6DE;
    color: #171819;
    gap: 120px;
    z-index: 0;
`;


const Title = styled.div`
    font-size: 100px;
    font-weight: 400;
    color: #fff;
    //text-shadow: 1px 1px 5px rgba(0,0,0,1);
    z-index: 1;
`;


const SubTitle = styled.div`
