import {React} from "react";
import {Link} from "react-router-dom";
import styled from "styled-components";
import Header from "./Header";


import Project_1 from "./Project_1.jpg";


const Project1 = () => {
    return (
        <PageContainer>
            <Header/>
            <img src={Project_1} style={{height: "100vh"}} alt="Project_1"/>
            <ExpContainer>
                <h1>"MADE"</h1>
                <h2>INTERACTIVE INSTALLATION</h2>
                <p>Interaction Design,
                    Interactive Installations,
                    Projection Mapping.
                    August 2021</p>
            </ExpContainer>
        </PageContainer>
    );

}

const PageContainer = styled.div`
    display: flex;
    //overflow: hidden;
    width: 100vw;
    flex-direction: column;
    justify-content: start;
    align-items: stretch;
    color: #fff;
    background-color: #171819;
`;

const LandingContainer = styled.div`
    scale: 1;
    background: linear-gradient(to right, #F857A6, #FF5858);
    height: 100%;
    text-align: start;
    display: flex;
    flex-direction: row;
    overflow: hidden;
    justify-content: start;
    align-items: start;
    color: #fff
`;

const ExpContainer = styled.div`
    overflow: hidden;
    text-align: start;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    grid-gap: 50px;
    justify-content: start;
    align-items: start;
    padding: 90px;

`;

const HomeContainer = styled.div`
    height: 100%;
    text-align: start;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    grid-gap: 10px;
    gap: 20px;
    justify-content: center;
    align-items: center;
    padding-inline: 95px;
    background-color: #171819;
    color: #fff;
    z-index: 0;
`;

const DescriptionContainer = styled.div`
    max-height: 100vh;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    padding-left: 90px;
    padding-right: 90px;
    padding-top: 90px;
    padding-bottom: 90px;
    gap: 90px;
    color: #fff;
    z-index: 0;
`;

const DescriptionContainerPink = styled.div`
    height: 850px;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: start;
    padding-left: 90px;
    padding-right: 90px;
    padding-top: 90px;
    padding-bottom: 90px;
    gap: 50px;
    color: #171819;
    background-color: #F857A6;
    z-index: 0;
`;

const MarkBrightPink = styled.mark`
    background-color: #FFC8E3;
    color: #171819;
    font-weight: 400;
`;

const MarkPink = styled.mark`
    background-color: #F857A6;
    color: #171819;
    font-weight: 400;
`;

const MarkOrange = styled.mark`
    background-color: #FF5858;
    color: #171819;
    font-weight: 400;
`;

const SeparatorContainer = styled.div`
    overflow: hidden;
    height: 300px;
    text-align: start;
    display: flex;
    flex-direction: row;
    justify-content: start;
    align-items: center;
    padding-left: 90px;
    background-color: #171819;
    color: #fff;
    z-index: 0;
`;

const Footer = styled.div`
    overflow: hidden;
    text-align: start;
    display: flex;
    flex-direction: column;
    justify-content: stretch;
    align-items: center;
    padding-top: 90px;
    padding-inline: 90px;
    padding-bottom: 50px;
    gap: 90px;
    background-color: #fff;
    color: #171819;
    z-index: 0;
`;

const Title = styled.div`
    font-size: 100px;
    font-weight: 400;
    color: #fff;
    //text-shadow: 1px 1px 5px rgba(0,0,0,1);
    z-index: 1;
`;


const SubTitle = styled.div`
    font-size: 32px;
    font-weight: 200;
    padding: 0px 10px;
    color: #fff;
    text-shadow: 1px 1px 5px rgba(0,0,0,0.5);
    z-index: 1;
`;


const StyledButton = styled.button`
    background-color: #0AD70A;
    color: #0a0a0a;
    //background-color: #fff;
    margin-top: 30px;
    margin-right: 10px;
    font-size: 18px;
    font-weight: 500;
    padding: 20px 40px;
    border: 2px solid #0AD70A;
    //border-radius: 30px;
    //text-shadow: 2px 1px 2px rgba(0,0,0,0.3);
    //box-shadow: 2px 4px 5px rgba(0,0,0,0.7);
    z-index: 1;
    transition: all 0.2s ease-in-out;
    &:hover {
        background-color: #0a0a0a;
        color: #0AD70A;
        border: 2px solid #0AD70A;
        transition: all 0.2s ease-in-out;
    }
`;


const Styledvideo = styled.video`
    position: absolute;
    top: -75px;
    left: -0px;
`;

const StyledLink = styled(Link)`
    color: #171819;
    text-decoration: none;
    position: relative;
    margin-top: 10px;
    padding-bottom: 10px;
`;

export default Project1;
