import {React} from'react';
import styled from "styled-components";
import {Link} from'react-router-dom';
import {faUser, faGolfBallTee} from "@fortawesome/free-solid-svg-icons";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import { useNavigate } from'react-router-dom';
import { useEffect, useState } from'react';

const Header = () => {
    const [isScrolled, setIsScrolled] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            if (window.scrollY > 0) {
                setIsScrolled(true);
            } else {
                setIsScrolled(false);
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return(
        <HeaderContainer  isScrolled={isScrolled}>
            {/*<div style={{display: 'flex', flexDirection:'row', gap:'50px'}}>*/}
                <Logo>
                    <FontAwesomeIcon icon={faGolfBallTee} size={'2xl'} style={{color: '#0AD70A'}}/>
                </Logo>
                <NavBar>
                    <StyledLink to={'/'}>Home</StyledLink>
                    <StyledLink to={'/mentors'}>AI-Mentors</StyledLink>
                    <StyledLink to={'/about'}>About Us</StyledLink>
                </NavBar>
            {/*</div>*/}
            <UserIcon/>
        </HeaderContainer>
    )
};

const HeaderContainer = styled.div`
    position: fixed;
    width: 100%;
    height: 100px;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    padding: 10px 50px;
    //text-shadow: 0 1px 5px #000000;
    font-weight: 400;
    z-index: 1000;
    transition: background-color 0.3s;
    background-color: ${(props) => (props.isScrolled ? 'rgba(10, 10, 10, 0.75)' : 'transparent')};
    backdrop-filter: ${(props) => (props.isScrolled ? 'blur(10px) brightness(120%) saturate(120%)' : 'none')};
`;

const StyledLink = styled(Link)`
    color: #fff;
    text-decoration: none;
    position: relative;
    margin-top: 10px;
    padding-bottom: 10px;

    &:hover::before {
        width: 100%;
    }

    &::before {
        content: '';
        position: absolute;
        bottom: 0;
        left: 0;
        width: 0;
        height: 2px;
        background-color: #0AD70A;
        transition: width 0.3s ease;
    }
`;

const Logo = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: start;
    width: 15%;

`;


const NavBar = styled.nav`
    display: flex;
    width: 30%;
    flex-direction: row;
    justify-content: space-between;
    align-items: stretch;

`;

const UserIcon = ({ home }) => {
    const navigate = useNavigate();

    const handleClick = () => {
        navigate(`/register`);
        window.location.reload();
    };

    const handleButtonClick = () => {
        navigate(`/log-in`);
        window.location.reload();
    };

    return (
        <UserContainer>
            {/*<FontAwesomeIcon icon={faUser} size="xl"/>*/}
            <div style={{paddingLeft: "20px"}} onClick={handleClick} >Register</div>
            <StyledButton onClick={handleButtonClick}>Log In</StyledButton>
        </UserContainer>
    );
};

const UserContainer = styled.div`
    display: flex;
    color: #fff;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    //margin-left: 120px;
    //padding: 0 15px;
    gap: 15px;
`;

const StyledButton = styled.button`
    background-color: #0a0a0a;
    color: #fff;
    font-size: 18px;
    font-weight: 400;
    padding: 10px 20px;
    border: 1px solid #fff;
    border-radius: 30px;
    //text-shadow: 1px 1px 5px rgba(0,0,0,0.7);
    box-shadow: 2px 4px 5px rgba(0,0,0,0.7);
    z-index: 1;
    cursor: pointer;
    transition: 0.5s;
    &:hover {
        background-color: #fff;
        color: #0a0a0a;
        transition: 0.5s;
    }
`;
export default Header;
